<?php    if (session_id() == '') {        session_start();    }        // $language is the language of site,    // worlds reference are in $language directory,    // one directory for every supported language    if(ISSET($_GET["language"])) {        $_SESSION["language"] = $_GET["language"];    }    if(ISSET($_SESSION["language"]) ) {        $language = $_SESSION["language"];    } else {        $language = "english";        $_SESSION["language"] = $language;    }        $directory = "";    $stylesheets = "";        $page_elements_name = $_SESSION["page_elements_name"];        // page blocks streams    $page_elements = array("header" => file_get_contents($page_elements_name["header"])        , "navigation" => file_get_contents($page_elements_name["navigation"])        , "main" => file_get_contents($page_elements_name["main"])        , "sidebar" => file_get_contents($page_elements_name["sidebar"])        , "footer" => file_get_contents($page_elements_name["footer"]));        // adjust directory    $page_elements["header"] = str_ireplace("\$directory = \"", "\$directory = \"" . $directory, $page_elements["header"]);    $page_elements["navigation"] = str_ireplace("\$directory = \"", "\$directory = \"" . $directory, $page_elements["navigation"]);    $page_elements["main"] = str_ireplace("\$directory = \"", "\$directory = \"" . $directory, $page_elements["main"]);    $page_elements["sidebar"] = str_ireplace("\$directory = \"", "\$directory = \"" . $directory, $page_elements["sidebar"]);    $page_elements["footer"] = str_ireplace("\$directory = \"", "\$directory = \"" . $directory, $page_elements["footer"]);?><!DOCTYPE html><html>    <head>        <title>GCR</title>                <!-- Favicon goes here -->        <link rel="gcr main icon" href="/gcr/images/gcr-favicon.ico">                <!-- Default style -->        <link href='<?php print($directory); ?>/gcr/css/base.css' rel='stylesheet' type='text/css'>            